import React from "react";
import Confetti from "react-dom-confetti";

class ConfettiButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      active: false
    };
    this.onClick = this.onClick.bind(this);
  }
  onClick() {
    // 'Confetti' only shows the confettig if 'active' turn from 'falsy' to 'truthy'.
    // We set it to 'true', and then immediately set it to 'false' again,
    // so we can hit the button multiple times.
    this.setState({ active: true }, function() {
      // immediately set 'active' to false again
      this.setState({ active: false });
    });
  }
  render() {
    const { active } = this.state;
    return (
      <div class="confetti-button" onClick={this.onClick}>
        <Confetti active={active} />
        <a
          className="rainbow-button js-scroll px-4"
          href="#work"
          role="button"
          onClick={() => setClicked(true)}
        >
          View My Work
        </a>
      </div>
    );
  }
}

export default ConfettiButton;
